import { Component, OnInit } from "@angular/core";
import { FormBuilder, FormGroup, Validators } from "@angular/forms";
import { Router, ActivatedRoute } from "@angular/router";

@Component({
  selector: "app-habit-form",
  templateUrl: "./habit-form.component.html",
  styleUrls: ["./habit-form.component.scss"],
})
export class HabitFormComponent implements OnInit {
  habitForm: FormGroup;
  isEditMode: boolean = false;
  habitId: string | null = null;
  loading: boolean = false;
  categories: string[] = [
    "Transportation",
    "Food",
    "Energy",
    "Waste",
    "Water",
    "Shopping",
  ];
  frequencies: string[] = ["daily", "weekly", "monthly", "one-time"];

  constructor(
    private formBuilder: FormBuilder,
    private router: Router,
    private route: ActivatedRoute
  ) {
    this.habitForm = this.formBuilder.group({
      description: ["", [Validators.required]],
      category: ["", [Validators.required]],
      impact: [1, [Validators.required, Validators.min(0)]],
      frequency: ["daily", [Validators.required]],
    });
  }

  ngOnInit(): void {
    // Check if we're in edit mode
    this.habitId = this.route.snapshot.paramMap.get("id");
    this.isEditMode = !!this.habitId;

    if (this.isEditMode && this.habitId) {
      this.loadHabitData(this.habitId);
    }
  }

  // In a real app, this would fetch data from an API
  loadHabitData(id: string): void {
    this.loading = true;

    // Mock data for demo - in a real app this would be an API call
    setTimeout(() => {
      // Find the habit in our mock data based on ID
      const habit = {
        _id: id,
        description: "Used public transportation instead of driving",
        category: "Transportation",
        impact: 3.2,
        frequency: "daily",
        isCompleted: false,
        date: new Date(),
      };

      // Patch the form with the habit data
      this.habitForm.patchValue({
        description: habit.description,
        category: habit.category,
        impact: habit.impact,
        frequency: habit.frequency,
      });

      this.loading = false;
    }, 1000);
  }

  onSubmit(): void {
    if (this.habitForm.invalid) {
      return;
    }

    const habitData = this.habitForm.value;

    // Add current date and completed status
    const completeHabitData = {
      ...habitData,
      date: new Date(),
      isCompleted: false,
    };

    if (this.isEditMode) {
      // In a real app, this would update the habit in the backend
      console.log("Updating habit:", completeHabitData);
    } else {
      // In a real app, this would create a new habit in the backend
      console.log("Creating new habit:", completeHabitData);
    }

    // Navigate back to habits list
    this.router.navigate(["/habits"]);
  }

  cancel(): void {
    this.router.navigate(["/habits"]);
  }
}
<div class="habit-form-container">
  <div class="habit-form-header">
    <h1>
      <i class="fas fa-leaf"></i>
      {{ isEditMode ? "Edit Habit" : "Add New Habit" }}
    </h1>
    <p>
      {{
        isEditMode
          ? "Update your sustainable habit details"
          : "Track a new eco-friendly habit"
      }}
    </p>
  </div>

  <!-- Loading State -->
  <div *ngIf="loading" class="loading-container">
    <div class="spinner-border text-primary" role="status">
      <span class="visually-hidden">Loading...</span>
    </div>
    <p>Loading habit data...</p>
  </div>

  <!-- Form -->
  <div *ngIf="!loading" class="habit-form-card">
    <form [formGroup]="habitForm" (ngSubmit)="onSubmit()">
      <!-- Description -->
      <div class="mb-3">
        <label for="description" class="form-label">Habit Description</label>
        <input
          type="text"
          id="description"
          formControlName="description"
          class="form-control"
          [ngClass]="{
            'is-invalid':
              habitForm.get('description')?.touched &&
              habitForm.get('description')?.invalid
          }"
          placeholder="Describe your sustainable habit"
        />
        <div
          *ngIf="
            habitForm.get('description')?.touched &&
            habitForm.get('description')?.invalid
          "
          class="invalid-feedback"
        >
          <div *ngIf="habitForm.get('description')?.errors?.['required']">
            Description is required
          </div>
        </div>
      </div>

      <!-- Category -->
      <div class="mb-3">
        <label for="category" class="form-label">Category</label>
        <select
          id="category"
          formControlName="category"
          class="form-select"
          [ngClass]="{
            'is-invalid':
              habitForm.get('category')?.touched &&
              habitForm.get('category')?.invalid
          }"
        >
          <option value="" disabled>Select a category</option>
          <option *ngFor="let category of categories" [value]="category">
            {{ category }}
          </option>
        </select>
        <div
          *ngIf="
            habitForm.get('category')?.touched &&
            habitForm.get('category')?.invalid
          "
          class="invalid-feedback"
        >
          <div *ngIf="habitForm.get('category')?.errors?.['required']">
            Category is required
          </div>
        </div>
      </div>

      <!-- Environmental Impact -->
      <div class="mb-3">
        <label for="impact" class="form-label"
          >Environmental Impact (kg CO₂ saved)</label
        >
        <input
          type="number"
          id="impact"
          formControlName="impact"
          class="form-control"
          [ngClass]="{
            'is-invalid':
              habitForm.get('impact')?.touched &&
              habitForm.get('impact')?.invalid
          }"
          min="0"
          step="0.1"
        />
        <div
          *ngIf="
            habitForm.get('impact')?.touched && habitForm.get('impact')?.invalid
          "
          class="invalid-feedback"
        >
          <div *ngIf="habitForm.get('impact')?.errors?.['required']">
            Impact value is required
          </div>
          <div *ngIf="habitForm.get('impact')?.errors?.['min']">
            Impact value must be positive
          </div>
        </div>
        <div class="form-text">
          Estimate the carbon footprint reduction from this habit
        </div>
      </div>

      <!-- Frequency -->
      <div class="mb-3">
        <label for="frequency" class="form-label">Frequency</label>
        <select
          id="frequency"
          formControlName="frequency"
          class="form-select"
          [ngClass]="{
            'is-invalid':
              habitForm.get('frequency')?.touched &&
              habitForm.get('frequency')?.invalid
          }"
        >
          <option *ngFor="let frequency of frequencies" [value]="frequency">
            {{ frequency.charAt(0).toUpperCase() + frequency.slice(1) }}
          </option>
        </select>
      </div>

      <!-- Form Actions -->
      <div class="habit-form-actions">
        <button
          type="button"
          class="btn btn-outline-secondary"
          (click)="cancel()"
        >
          Cancel
        </button>
        <button
          type="submit"
          class="btn btn-primary"
          [disabled]="habitForm.invalid"
        >
          {{ isEditMode ? "Update Habit" : "Save Habit" }}
        </button>
      </div>
    </form>
  </div>
</div>
