import { Component } from "@angular/core";
import { FormBuilder, FormGroup, Validators } from "@angular/forms";
import { Router } from "@angular/router";
import { UserService } from "../../services/user.service";

@Component({
  selector: "app-login",
  templateUrl: "./login.component.html",
  styleUrls: ["./login.component.scss"],
})
export class LoginComponent {
  loginForm!: FormGroup;
  errorMessage: string = "";
  isLoading: boolean = false;

  constructor(
    private fb: FormBuilder,
    private userService: UserService,
    private router: Router
  ) {
    this.loginForm = this.fb.group({
      email: ["", [Validators.required, Validators.email]],
      password: ["", [Validators.required, Validators.minLength(6)]],
    });
  }

  onSubmit(): void {
    if (this.loginForm.invalid) return;

    this.isLoading = true;
    this.errorMessage = "";

    this.userService.login(this.loginForm.value).subscribe({
      next: () => {
        this.router.navigate(["/dashboard"]);
      },
      error: (error) => {
        this.errorMessage =
          error.error?.message ||
          "Login failed. Please check your credentials.";
        this.isLoading = false;
      },
      complete: () => {
        this.isLoading = false;
      },
    });
  }
}
<div class="login-container">
  <div class="login-card">
    <h2 class="login-title">
      <i class="fas fa-leaf text-success"></i> EcoWise AI
    </h2>
    <h3>Login to your account</h3>

    <div *ngIf="errorMessage" class="alert alert-danger">
      {{ errorMessage }}
    </div>

    <form [formGroup]="loginForm" (ngSubmit)="onSubmit()">
      <div class="mb-3">
        <label for="email" class="form-label">Email</label>
        <input
          type="email"
          id="email"
          formControlName="email"
          class="form-control"
          [ngClass]="{
            'is-invalid':
              loginForm.get('email')?.touched && loginForm.get('email')?.invalid
          }"
          placeholder="Enter your email"
        />
        <div
          class="invalid-feedback"
          *ngIf="loginForm.get('email')?.touched && loginForm.get('email')?.errors?.['required']"
        >
          Email is required
        </div>
        <div
          class="invalid-feedback"
          *ngIf="loginForm.get('email')?.touched && loginForm.get('email')?.errors?.['email']"
        >
          Please enter a valid email address
        </div>
      </div>

      <div class="mb-3">
        <label for="password" class="form-label">Password</label>
        <input
          type="password"
          id="password"
          formControlName="password"
          class="form-control"
          [ngClass]="{
            'is-invalid':
              loginForm.get('password')?.touched &&
              loginForm.get('password')?.invalid
          }"
          placeholder="Enter your password"
        />
        <div
          class="invalid-feedback"
          *ngIf="loginForm.get('password')?.touched && loginForm.get('password')?.errors?.['required']"
        >
          Password is required
        </div>
        <div
          class="invalid-feedback"
          *ngIf="loginForm.get('password')?.touched && loginForm.get('password')?.errors?.['minlength']"
        >
          Password must be at least 6 characters
        </div>
      </div>

      <button
        type="submit"
        class="btn btn-primary w-100 mt-3"
        [disabled]="loginForm.invalid || isLoading"
      >
        <span
          *ngIf="isLoading"
          class="spinner-border spinner-border-sm me-2"
          role="status"
          aria-hidden="true"
        ></span>
        Login
      </button>
    </form>

    <p class="mt-3 text-center">
      Don't have an account? <a routerLink="/register">Register</a>
    </p>
  </div>
</div>
