import { Component, OnInit } from "@angular/core";
import { FormBuilder, FormGroup, Validators } from "@angular/forms";
import { Router } from "@angular/router";
import { UserService } from "../../services/user.service";

@Component({
  selector: "app-register",
  templateUrl: "./register.component.html",
  styleUrls: ["./register.component.scss"],
})
export class RegisterComponent implements OnInit {
  registerForm!: FormGroup;
  errorMessage: string = "";
  loading: boolean = false;
  passwordsMatch: boolean = true;

  constructor(
    private formBuilder: FormBuilder,
    private userService: UserService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.registerForm = this.formBuilder.group({
      name: ["", [Validators.required]],
      email: ["", [Validators.required, Validators.email]],
      password: ["", [Validators.required, Validators.minLength(6)]],
      confirmPassword: ["", [Validators.required]],
    });

    // Check password match on form changes
    this.registerForm.valueChanges.subscribe(() => {
      const password = this.registerForm.get("password")?.value;
      const confirmPassword = this.registerForm.get("confirmPassword")?.value;
      this.passwordsMatch =
        !password || !confirmPassword || password === confirmPassword;
    });
  }

  onSubmit(): void {
    if (this.registerForm.invalid || !this.passwordsMatch) {
      return;
    }

    const password = this.registerForm.get("password")?.value;
    const confirmPassword = this.registerForm.get("confirmPassword")?.value;

    if (password !== confirmPassword) {
      this.errorMessage = "Passwords do not match";
      return;
    }

    this.loading = true;
    this.errorMessage = "";

    const { name, email, password: userPassword } = this.registerForm.value;

    this.userService.register({ name, email, password: userPassword }).subscribe({
      next: () => {
        this.loading = false;
        this.router.navigate(["/login"], {
          queryParams: { registered: "true" },
        });
      },
      error: (error) => {
        this.loading = false;
        this.errorMessage =
          error.error?.message || "Registration failed. Please try again.";
      },
    });
  }
}
<div class="register-container">
  <div class="register-card">
    <div class="register-title">
      <h3><i class="fas fa-user-plus"></i> Create an Account</h3>
      <p>Join EcoWise and start your sustainability journey</p>
    </div>

    <div *ngIf="errorMessage" class="alert alert-danger">
      {{ errorMessage }}
    </div>

    <form [formGroup]="registerForm" (ngSubmit)="onSubmit()">
      <!-- Name -->
      <div class="mb-3">
        <label for="name" class="form-label">Full Name</label>
        <input
          type="text"
          id="name"
          formControlName="name"
          class="form-control"
          [ngClass]="{
            'is-invalid':
              registerForm.get('name')?.touched &&
              registerForm.get('name')?.invalid
          }"
          placeholder="Enter your full name"
        />
        <div
          *ngIf="
            registerForm.get('name')?.touched &&
            registerForm.get('name')?.invalid
          "
          class="invalid-feedback"
        >
          <div *ngIf="registerForm.get('name')?.errors?.['required']">
            Name is required
          </div>
        </div>
      </div>

      <!-- Email -->
      <div class="mb-3">
        <label for="email" class="form-label">Email address</label>
        <input
          type="email"
          id="email"
          formControlName="email"
          class="form-control"
          [ngClass]="{
            'is-invalid':
              registerForm.get('email')?.touched &&
              registerForm.get('email')?.invalid
          }"
          placeholder="Enter your email"
        />
        <div
          *ngIf="
            registerForm.get('email')?.touched &&
            registerForm.get('email')?.invalid
          "
          class="invalid-feedback"
        >
          <div *ngIf="registerForm.get('email')?.errors?.['required']">
            Email is required
          </div>
          <div *ngIf="registerForm.get('email')?.errors?.['email']">
            Please enter a valid email address
          </div>
        </div>
      </div>

      <!-- Password -->
      <div class="mb-3">
        <label for="password" class="form-label">Password</label>
        <input
          type="password"
          id="password"
          formControlName="password"
          class="form-control"
          [ngClass]="{
            'is-invalid':
              registerForm.get('password')?.touched &&
              registerForm.get('password')?.invalid
          }"
          placeholder="Create a password"
        />
        <div
          *ngIf="
            registerForm.get('password')?.touched &&
            registerForm.get('password')?.invalid
          "
          class="invalid-feedback"
        >
          <div *ngIf="registerForm.get('password')?.errors?.['required']">
            Password is required
          </div>
          <div *ngIf="registerForm.get('password')?.errors?.['minlength']">
            Password must be at least 6 characters
          </div>
        </div>
      </div>

      <!-- Confirm Password -->
      <div class="mb-3">
        <label for="confirmPassword" class="form-label">Confirm Password</label>
        <input
          type="password"
          id="confirmPassword"
          formControlName="confirmPassword"
          class="form-control"
          [ngClass]="{
            'is-invalid':
              (registerForm.get('confirmPassword')?.touched &&
                registerForm.get('confirmPassword')?.invalid) ||
              !passwordsMatch
          }"
          placeholder="Confirm your password"
        />
        <div
          *ngIf="
            registerForm.get('confirmPassword')?.touched &&
            registerForm.get('confirmPassword')?.invalid
          "
          class="invalid-feedback"
        >
          <div
            *ngIf="registerForm.get('confirmPassword')?.errors?.['required']"
          >
            Please confirm your password
          </div>
        </div>
        <div *ngIf="!passwordsMatch" class="invalid-feedback d-block">
          Passwords do not match
        </div>
      </div>

      <div class="d-grid gap-2">
        <button
          type="submit"
          class="btn btn-primary"
          [disabled]="registerForm.invalid || loading || !passwordsMatch"
        >
          <span
            *ngIf="loading"
            class="spinner-border spinner-border-sm me-2"
            role="status"
            aria-hidden="true"
          ></span>
          Create Account
        </button>
      </div>
    </form>

    <div class="mt-3 text-center">
      <p>Already have an account? <a routerLink="/login">Log in</a></p>
    </div>
  </div>
</div>
