import { Component, OnInit } from "@angular/core";
import { FormBuilder, FormGroup, Validators } from "@angular/forms";
import { Router } from "@angular/router";
import { UserService } from "../../services/user.service";

@Component({
  selector: "app-register",
  templateUrl: "./register.component.html",
  styleUrls: ["./register.component.scss"],
})
export class RegisterComponent implements OnInit {
  registerForm: FormGroup;
  submitted = false;
  errorMessage: string = "";
  loading: boolean = false;
  passwordsMatch: boolean = true;

  constructor(
    private fb: FormBuilder,
    private userService: UserService,
    private router: Router
  ) {
    this.registerForm = this.fb.group({
      firstName: ['', [Validators.required, Validators.minLength(2)]],
      lastName: ['', [Validators.required, Validators.minLength(2)]],
      username: ['', [Validators.required, Validators.minLength(3)]],
      email: ['', [Validators.required, Validators.email]],
      password: ['', [Validators.required, Validators.minLength(6)]],
      confirmPassword: ["", [Validators.required]],
    });

    // Check password match on form changes
    this.registerForm.valueChanges.subscribe(() => {
      const password = this.registerForm.get("password")?.value;
      const confirmPassword = this.registerForm.get("confirmPassword")?.value;
      this.passwordsMatch =
        !password || !confirmPassword || password === confirmPassword;
    });
  }

  // Getter for easy access to form fields
  get f() { return this.registerForm.controls; }

  ngOnInit(): void {}

  onSubmit(): void {
    this.submitted = true;

    if (this.registerForm.invalid || !this.passwordsMatch) {
      return;
    }

    const password = this.registerForm.get("password")?.value;
    const confirmPassword = this.registerForm.get("confirmPassword")?.value;

    if (password !== confirmPassword) {
      this.errorMessage = "Passwords do not match";
      return;
    }

    this.loading = true;
    this.errorMessage = "";

    const userData = {
      firstName: this.registerForm.get('firstName')?.value,
      lastName: this.registerForm.get('lastName')?.value,
      username: this.registerForm.get('username')?.value,
      email: this.registerForm.get('email')?.value,
      password: this.registerForm.get('password')?.value
    };

    this.userService.register(userData).subscribe({
      next: (response) => {
        this.loading = false;
        this.router.navigate(['/login']);
      },
      error: (error) => {
        this.loading = false;
        this.errorMessage =
          error.error?.message || "Registration failed. Please try again.";
      }
    });
  }
}
<div class="register-container">
  <div class="register-card">
    <div class="register-title">
      <h3><i class="fas fa-user-plus"></i> Create an Account</h3>
      <p>Join EcoWise and start your sustainability journey</p>
    </div>

    <div *ngIf="errorMessage" class="alert alert-danger">
      {{ errorMessage }}
    </div>

    <form [formGroup]="registerForm" (ngSubmit)="onSubmit()">
      <div class="form-group">
        <label for="firstName">First Name</label>
        <input 
          type="text" 
          id="firstName"
          formControlName="firstName" 
          class="form-control"
          [ngClass]="{ 'is-invalid': submitted && f['firstName'].errors }"
          placeholder="Enter first name"
        >
        <div *ngIf="submitted && f['firstName'].errors" class="invalid-feedback">
          <div *ngIf="f['firstName'].errors['required']">First Name is required</div>
          <div *ngIf="f['firstName'].errors['minlength']">First Name must be at least 2 characters</div>
        </div>
      </div>

      <div class="form-group">
        <label for="lastName">Last Name</label>
        <input 
          type="text" 
          id="lastName"
          formControlName="lastName" 
          class="form-control"
          [ngClass]="{ 'is-invalid': submitted && f['lastName'].errors }"
          placeholder="Enter last name"
        >
        <div *ngIf="submitted && f['lastName'].errors" class="invalid-feedback">
          <div *ngIf="f['lastName'].errors['required']">Last Name is required</div>
          <div *ngIf="f['lastName'].errors['minlength']">Last Name must be at least 2 characters</div>
        </div>
      </div>

      <div class="form-group">
        <label for="username">Username</label>
        <input 
          type="text" 
          id="username"
          formControlName="username" 
          class="form-control"
          [ngClass]="{ 'is-invalid': submitted && f['username'].errors }"
          placeholder="Enter username"
        >
        <div *ngIf="submitted && f['username'].errors" class="invalid-feedback">
          <div *ngIf="f['username'].errors['required']">Username is required</div>
          <div *ngIf="f['username'].errors['minlength']">Username must be at least 3 characters</div>
        </div>
      </div>

      <div class="form-group">
        <label for="email">Email</label>
        <input 
          type="email" 
          id="email"
          formControlName="email" 
          class="form-control"
          [ngClass]="{ 'is-invalid': submitted && f['email'].errors }"
          placeholder="Enter email"
        >
        <div *ngIf="submitted && f['email'].errors" class="invalid-feedback">
          <div *ngIf="f['email'].errors['required']">Email is required</div>
          <div *ngIf="f['email'].errors['email']">Please enter a valid email address</div>
        </div>
      </div>

      <div class="form-group">
        <label for="password">Password</label>
        <input 
          type="password" 
          id="password"
          formControlName="password" 
          class="form-control"
          [ngClass]="{ 'is-invalid': submitted && f['password'].errors }"
          placeholder="Enter password"
        >
        <div *ngIf="submitted && f['password'].errors" class="invalid-feedback">
          <div *ngIf="f['password'].errors['required']">Password is required</div>
          <div *ngIf="f['password'].errors['minlength']">Password must be at least 6 characters</div>
        </div>
      </div>

      <div class="d-grid gap-2">
        <button
          type="submit"
          class="btn btn-primary w-100"
          [disabled]="registerForm.invalid"
        >
          Create Account
        </button>
      </div>
    </form>

    <div class="mt-3 text-center">
      <p>Already have an account? <a routerLink="/login">Log in</a></p>
    </div>
  </div>
</div>
